mapper.select:
SELECT VARIABLE_1, VARIABLE_2, VARIABLE_3, DESV_ORIGINAL, CV_ORIGINAL, RI_ORIGINAL, DESV_CORREGIDO, CV_CORREGIDO, RI_CORREGIDO, INDICADOR_ID, VAR_EXPLICADA, ANIO FROM MD_CORRECCION_ESTADISTICAS
AND VARIABLE_1 = #{variable1}
AND VARIABLE_2 = #{variable2}
AND VARIABLE_3 = #{variable3}
AND DESV_ORIGINAL = #{desvOriginal}
AND CV_ORIGINAL = #{cvOriginal}
AND RI_ORIGINAL = #{riOriginal}
AND DESV_CORREGIDO = #{desvCorregido}
AND CV_CORREGIDO = #{cvCorregido}
AND RI_CORREGIDO = #{riCorregido}
AND INDICADOR_ID = #{indicadorId}
AND ANIO = #{anio}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_CORRECCION_ESTADISTICAS


	AND 
		VARIABLE_1 = #{variable1}


	AND 
		VARIABLE_2 = #{variable2}


	AND 
		VARIABLE_3 = #{variable3}


	AND 
		DESV_ORIGINAL = #{desvOriginal}


	AND 
		CV_ORIGINAL = #{cvOriginal}


	AND 
		RI_ORIGINAL = #{riOriginal}


	AND 
		DESV_CORREGIDO = #{desvCorregido}


	AND 
		CV_CORREGIDO = #{cvCorregido}


	AND 
		RI_CORREGIDO = #{riCorregido}


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		ANIO = #{anio}

mapper.delete:
DELETE FROM MD_CORRECCION_ESTADISTICAS
WHERE 1=1

		INDICADOR_ID = #{indicadorId}


		ANIO = #{anio}
