mapper.select:
SELECT CE.TIPO_HOSPITAL AS TIPO_HOSPITAL, CE.CLUSTER_HOSPITAL AS CLUSTER_HOSPITAL, CE.VALOR_ORIGINAL AS VALOR_ORIGINAL, CE.VALOR_CORREGIDO AS VALOR_CORREGIDO, CE.CORRECCION AS CORRECCION, CE.INDICADOR_ID AS INDICADOR_ID, 'variable'||VIC1.VARIABLE_INFLUENCIA_ID||'.codigo'||VIC1.CODIGO AS TIPO_HOSPITAL_DESC, 'variable'||VIC2.VARIABLE_INFLUENCIA_ID||'.codigo'||VIC2.CODIGO AS CLUSTER_HOSPITAL_DESC, H.DESCRIPCION AS NOMBRE_HOSPITAL, H.CODIGO_HOSPITAL AS CODIGO_HOSPITAL, C.CODIGO_CCAA AS CODIGO_CCAA, C.DESCRIPCION AS DESC_CCAA, CE.ANIO AS ANIO FROM MD_CORRECCION_INDIVIDUAL CE, MD_VARS_INF_CODIGOS VIC1, MD_VARS_INF_CODIGOS VIC2, MD_HOSPITALES H, MD_CCAA C, MD_PROVINCIAS P WHERE VIC1.CODIGO LIKE CE.TIPO_HOSPITAL AND VIC2.CODIGO LIKE CE.CLUSTER_HOSPITAL AND VIC1.VARIABLE_INFLUENCIA_ID = 1 AND VIC2.VARIABLE_INFLUENCIA_ID = 12 AND CE.CODIGO_HOSPITAL = H.CODIGO_HOSPITAL AND H.PROVINCIA_HOSPITAL = P.CODIGO_PROVINCIA AND P.CODIGO_CCAA = C.CODIGO_CCAA
AND CE.TIPO_HOSPITAL LIKE #{tipoHospital}
AND CE.CLUSTER_HOSPITAL LIKE #{clusterHospital}
AND CE.VALOR_ORIGINAL = #{valorOriginal}
AND CE.VALOR_CORREGIDO = #{valorCorregido}
AND CE.CORRECCION = #{correccion}
AND CE.INDICADOR_ID = #{indicadorId}
AND CE.ANIO = #{anio}
AND P.CODIGO_CCAA = #{codigoCcaa}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_CORRECCION_INDIVIDUAL


	AND 
		TIPO_HOSPITAL LIKE #{tipoHospital}


	AND 
		CLUSTER_HOSPITAL LIKE #{clusterHospital}


	AND 
		VALOR_ORIGINAL = #{valorOriginal}


	AND 
		VALOR_CORREGIDO = #{valorCorregido}


	AND 
		CORRECCION = #{correccion}


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		CE.ANIO = #{anio}

mapper.update:
UPDATE MD_CORRECCION_INDIVIDUAL
SET
	TIPO_HOSPITAL = #{tipoHospital},
	CLUSTER_HOSPITAL = #{clusterHospital},
	VALOR_ORIGINAL = #{valorOriginal},
	VALOR_CORREGIDO = #{valorCorregido},
	CORRECCION = #{correccion},
	INDICADOR_ID = #{indicadorId},
	ANIO = #{anio}
WHERE
	TIPO_HOSPITAL = #{tipoHospital}
AND
	CLUSTER_HOSPITAL = #{clusterHospital}
AND
	VALOR_ORIGINAL = #{valorOriginal}
AND
	VALOR_CORREGIDO = #{valorCorregido}
AND
	INDICADOR_ID = #{indicadorId}
AND
	ANIO = #{anio}

mapper.delete:
DELETE FROM MD_CORRECCION_INDIVIDUAL
WHERE (1=1)
AND 
	TIPO_HOSPITAL = #{tipoHospital}

AND 
	CLUSTER_HOSPITAL = #{clusterHospital}

AND 
	VALOR_ORIGINAL = #{valorOriginal}

AND 
	VALOR_CORREGIDO = #{valorCorregido}

AND 
	INDICADOR_ID = #{indicadorId}

AND 
	ANIO = #{anio}
