mapper.select:
SELECT EJE_ANALISIS_ID, NOMBRE, DESCRIPCION, ACTIVO, ETIQUETA FROM MD_EJES_ANALISIS
AND EJE_ANALISIS_ID = #{ejeAnalisisId}
AND NOMBRE LIKE #{nombre}
AND DESCRIPCION LIKE #{descripcion}
AND ACTIVO = #{activo}
AND ETIQUETA LIKE #{etiqueta}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_EJES_ANALISIS


	AND 
		EJE_ANALISIS_ID = #{ejeAnalisisId}


	AND 
		NOMBRE LIKE #{nombre}


	AND 
		DESCRIPCION LIKE #{descripcion}


	AND 
		ACTIVO = #{activo}


	AND 
		ETIQUETA LIKE #{etiqueta}
