mapper.select:
SELECT INDICADOR_ID, EJE_ANALISIS_ID, MD_EJES_ANALISIS_INDICADORES.ANIO AS ANIO FROM MD_EJES_ANALISIS_INDICADORES INNER JOIN MD_ANIOS ON MD_EJES_ANALISIS_INDICADORES.ANIO=MD_ANIOS.ANIO
MD_ANIOS.ACTIVO = 1 AND AND INDICADOR_ID = #{indicadorId}
AND EJE_ANALISIS_ID = #{ejeAnalisisId}
AND ANIO = #{anio}
ORDER BY MD_EJES_ANALISIS_INDICADORES.ANIO

SELECT INDICADOR_ID, EJE_ANALISIS_ID, ANIO AS ANIO FROM MD_EJES_ANALISIS_INDICADORES
AND INDICADOR_ID = #{indicadorId}
AND EJE_ANALISIS_ID = #{ejeAnalisisId}
AND ANIO = #{anio}
ORDER BY ANIO

SELECT
	COUNT(*) AS COUNT
FROM
	MD_EJES_ANALISIS_INDICADORES


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		EJE_ANALISIS_ID = #{ejeAnalisisId}


	AND 
		ANIO = #{anio}

mapper.update:
UPDATE MD_EJES_ANALISIS_INDICADORES
SET
	INDICADOR_ID = #{indicadorId},
	EJE_ANALISIS_ID = #{ejeAnalisisId},
	ANIO = #{anio}
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	EJE_ANALISIS_ID = #{ejeAnalisisId}
AND
	ANIO = #{anio}

mapper.delete:
DELETE FROM MD_EJES_ANALISIS_INDICADORES
WHERE (1=1)
AND 
	INDICADOR_ID = #{indicadorId}


AND 
	EJE_ANALISIS_ID = #{ejeAnalisisId}


AND 
	ANIO = #{anio}
