mapper.select:
SELECT INDICADOR_ID, NOMBRE, GRUPO_INDICADOR_ID, NOMBRE_COMPLETO, DESCRIPCION, ACTIVO, ETIQUETA, CODIGO_TIPO_INDICADOR, METADATA FROM MD_INDICADORES
AND INDICADOR_ID = #{indicadorId}
AND NOMBRE LIKE #{nombre}
AND GRUPO_INDICADOR_ID = #{grupoIndicadorId}
AND NOMBRE_COMPLETO LIKE #{nombreCompleto}
AND DESCRIPCION LIKE #{descripcion}
AND ACTIVO = #{activo}
AND ETIQUETA = #{etiqueta}
AND CODIGO_TIPO_INDICADOR = #{tipoIndicador}
AND METADATA = #{metadata}
ORDER BY PESO ASC

SELECT
	COUNT(*) AS COUNT
FROM
	MD_INDICADORES


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		NOMBRE LIKE #{nombre}


	AND 
		GRUPO_INDICADOR_ID = #{grupoIndicadorId}


	AND 
		NOMBRE_COMPLETO LIKE #{nombreCompleto}


	AND 
		DESCRIPCION LIKE #{descripcion}


	AND 
		ACTIVO = #{activo}


	AND 
		ETIQUETA = #{etiqueta}


	AND 
		CODIGO_TIPO_INDICADOR = #{tipoIndicador}


	AND 
		METADATA = #{metadata}
