mapper.select:
SELECT VIE.valor AS VALOR, VIE.indicador_id AS INDICADOR_ID, VIE.variable_id AS VARIABLE_ID, VIE.codigo AS CODIGO, VI.nombre AS VARIABLE_DESC, VIC.descripcion AS ESTADO_DESC FROM MD_VALOR_INDICADOR_ESTADOS VIE, MD_VARS_INF_CODIGOS VIC, MD_VARS_INF VI
AND VALOR = #{valorIndicador}
AND INDICADOR_ID = #{indicadorId}
AND VARIABLE_ID = #{variableId}
AND CODIGO LIKE #{estado}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_VALOR_INDICADOR_ESTADOS


	AND 
		VALOR = #{valorIndicador}


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		VARIABLE_ID = #{variableId}


	AND 
		ESTADO LIKE #{estado}

mapper.update:
UPDATE MD_VALOR_INDICADOR_ESTADOS
SET
	VARIABLE_ID = #{variableId},
	INDICADOR_ID = #{indicadorId},
	CODIGO = #{estado},
	VALOR = #{valorIndicador}
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_ID = #{variableId}
AND
	CODIGO = #{estado}

mapper.delete:
DELETE FROM MD_VALOR_INDICADOR_ESTADOS
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_ID = #{variableId}
AND
	CODIGO = #{estado}
