mapper.select:
SELECT VALOR_INDICADOR, VARIACION_ABSOLUTA, VARIACION_RELATIVA, FRECUENCIA, INDICADOR_ID, VARIABLE_ID, ESTADO FROM MD_RELEVANCIA_ESTADOS_DEC
AND VALOR_INDICADOR = #{valorIndicador}
AND VARIACION_ABSOLUTA = #{variacionAbsoluta}
AND VARIACION_RELATIVA = #{variacionRelativa}
AND FRECUENCIA = #{frecuencia}
AND INDICADOR_ID = #{indicadorId}
AND VARIABLE_ID = #{variableId}
AND ESTADO LIKE #{estado}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_RELEVANCIA_ESTADOS_DEC


	AND 
		VALOR_INDICADOR = #{valorIndicador}


	AND 
		VARIACION_ABSOLUTA = #{variacionAbsoluta}


	AND 
		VARIACION_RELATIVA = #{variacionRelativa}


	AND 
		FRECUENCIA = #{frecuencia}


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		VARIABLE_ID = #{variableId}


	AND 
		ESTADO LIKE #{estado}

mapper.delete:
DELETE FROM MD_RELEVANCIA_ESTADOS_DEC
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_ID = #{variableId}
AND
	ESTADO = #{estado}
