mapper.select:
SELECT VARIACION_MEDIA_RELATIVA, VARIACION_MAX_RELATIVA, INDICADOR_ID, VARIABLE_ID_1, VARIABLE_ID_2 FROM MD_RELEVANCIA_VARIABLES_PARES
AND VARIACION_MEDIA_RELATIVA = #{variacionMediaRelativa}
AND VARIACION_MAX_RELATIVA = #{variacionMaxRelativa}
AND INDICADOR_ID = #{indicadorId}
AND VARIABLE_ID_1 = #{variableId1}
AND VARIABLE_ID_2 = #{variableId2}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_RELEVANCIA_VARIABLES_PARES


	AND 
		VARIACION_MEDIA_RELATIVA = #{variacionMediaRelativa}


	AND 
		VARIACION_MAX_RELATIVA = #{variacionMaxRelativa}


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		VARIABLE_ID_1 = #{variableId1}


	AND 
		VARIABLE_ID_2 = #{variableId2}

mapper.update:
UPDATE MD_RELEVANCIA_VARIABLES_PARES
SET
	VARIACION_MEDIA_RELATIVA = #{variacionMediaRelativa},
	VARIACION_MAX_RELATIVA = #{variacionMaxRelativa},
	INDICADOR_ID = #{indicadorId},
	VARIABLE_ID_1 = #{variableId1},
	VARIABLE_ID_2 = #{variableId2}
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_ID_1 = #{variableId1}
AND
	VARIABLE_ID_2 = #{variableId2}

mapper.delete:
DELETE FROM MD_RELEVANCIA_VARIABLES_PARES
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_ID_1 = #{variableId1}
AND
	VARIABLE_ID_2 = #{variableId2}
