mapper.select:
SELECT U_ID, UNAME, MAIL, PASSWORD, PADRE_ID, ACTIVE, PASSWORD_CHANGED, LAST_PASSWORDS, NAMES, TOKEN, CUSTOM_FILTER FROM MD_USUARIOS
AND U_ID = #{id}
AND U_ID in ( $idList[]$ )
AND UNAME LIKE #{name}
AND MAIL LIKE #{mail}
AND PASSWORD LIKE #{password}
AND PADRE_ID = #{padreId}
AND ACTIVE = #{active}
AND NAMES = #{nombreApellidos}
AND TOKEN = #{token}
AND CUSTOM_FILTER = #{customFilter}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_USUARIOS


	AND 
		U_ID = #{id}


	AND 
		UNAME LIKE #{name}


	AND 
		MAIL LIKE #{mail}


	AND 
		PASSWORD LIKE #{password}


	AND 
		PADRE_ID = #{padreId}


	AND 
		ACTIVE = #{active}


	AND 
		NAMES = #{nombreApellidos}


	AND 
		TOKEN = #{token}


	AND 
		CUSTOM_FILTER = #{customFilter}

mapper.insert:
INSERT INTO MD_USUARIOS
	(
		U_ID
		UNAME
		MAIL
		PASSWORD
		PADRE_ID
		ACTIVE
		PASSWORD_CHANGED
		LAST_PASSWORDS
		NAMES
		TOKEN
		CUSTOM_FILTER
	)
VALUES
	(
		(SELECT max(U_ID) + 1 FROM MD_USUARIOS)
		#{name}
		#{mail}
		#{password}
		#{padreId}
		#{active}
		#{passwordChanged}
		#{lastPasswords}
		#{nombreApellidos}
		#{token}
		#{customFilter}
	)

mapper.update:
UPDATE MD_USUARIOS
SET
	U_ID = #{id}
	UNAME = #{name}
	MAIL = #{mail}
	PASSWORD = #{password}
	PADRE_ID = #{padreId}
	ACTIVE   = #{active}
	PASSWORD_CHANGED = #{passwordChanged}
	LAST_PASSWORDS = #{lastPasswords}
	NAMES = #{nombreApellidos}
	TOKEN = #{token}
	CUSTOM_FILTER = #{customFilter}
WHERE
	U_ID = #{id}

mapper.delete:
DELETE FROM MD_USUARIOS


	AND 
