mapper.select:
SELECT UR.U_ID, UR.RID, UR.INHERIT, R.RNAME, U.UNAME FROM MD_USUARIOSROLES UR, MD_USUARIOS U, MD_ROLES R WHERE UR.RID = R.RID AND UR.U_ID = U.U_ID
AND U.U_ID = #{uid}
AND R.RID = #{rid}
AND UR.INHERIT = #{inherit}
AND U.UNAME LIKE #{userName}
AND R.RNAME LIKE #{roleName}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_USUARIOSROLES


	AND 
		U_ID = #{uid}


	AND 
		INHERIT = #{inherit}


	AND 
		RID LIKE #{rid}

mapper.delete:
DELETE FROM MD_USUARIOSROLES
WHERE
	U_ID IN(
		SELECT
			U_ID
			FROM MD_USUARIOS



				AND 
					U_ID = #{id}


				AND 
					UNAME LIKE #{name}


				AND 
					MAIL LIKE #{mail}


				AND 
					PASSWORD LIKE #{password}


				AND 
					PADRE_ID = #{padreId}


				AND 
					ACTIVE = #{active}



	)
