mapper.select:
SELECT VARIABLE_INFLUENCIA_ID, NOMBRE, DESCRIPCION, ACTIVO, ETIQUETA, ORDER_BY_FIELD, GROUP_BY_FIELD, SELECT_FIELDS, IS_IN_PART, IS_IN_BAYES, 0 as ORDEN, 0 as PESO, 0 as APLICA_FILTRO, 0 as APLICA_AGRUPACION, 0 as INFLUYE_BAYES, 0 as CORRIGE_BAYES
FROM MD_VARS_INF
AND VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}
AND NOMBRE LIKE #{nombre}
AND DESCRIPCION LIKE #{descripcion}
AND ACTIVO = #{activo}
AND ETIQUETA LIKE #{etiqueta}
AND ORDER_BY_FIELD LIKE #{orderByField}
AND GROUP_BY_FIELD LIKE #{groupByField}
AND SELECT_FIELDS LIKE #{selectFields}
AND IS_IN_PART = #{isInPart}
AND IS_IN_BAYES = #{isInBayes}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_VARS_INF


	AND 
		VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}


	AND 
		NOMBRE LIKE #{nombre}


	AND 
		DESCRIPCION LIKE #{descripcion}


	AND 
		ACTIVO = #{activo}


	AND 
		ETIQUETA LIKE #{etiqueta}


	AND 
		ORDER_BY_FIELD LIKE #{orderByField}


	AND 
		GROUP_BY_FIELD LIKE #{groupByField}


	AND 
		SELECT_FIELDS LIKE #{selectFields}


	AND 
		IS_IN_PART = #{isInPart}


	AND 
		IS_IN_BAYES = #{isInBayes}
