mapper.select:
SELECT VI.VARIABLE_INFLUENCIA_ID, VI.CODIGO, VI.VALOR, VI.ORDEN, VI.DESCRIPCION, V.NOMBRE AS VARIABLE_INFLUENCIA_DESC FROM MD_VARS_INF_CODIGOS VI, MD_VARS_INF V
WHERE V.VARIABLE_INFLUENCIA_ID = VI.VARIABLE_INFLUENCIA_ID
AND VI.VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}
AND VI.CODIGO LIKE #{codigo}
AND VI.VALOR LIKE #{valor}
AND VI.ORDEN = #{orden}
AND VI.DESCRIPCION LIKE #{descripcion}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_VARS_INF_CODIGOS


	AND 
		VI.VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}


	AND 
		VI.CODIGO LIKE #{codigo}


	AND 
		VI.VALOR LIKE #{valor}


	AND 
		VI.ORDEN = #{orden}


	AND 
		VI.DESCRIPCION LIKE #{descripcion}

mapper.delete:
DELETE FROM MD_VARS_INF_CODIGOS
WHERE
	VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}


		AND CODIGO LIKE #{codigo}
