mapper.select:
SELECT VI.INDICADOR_ID, VI.VARIABLE_INFLUENCIA_ID, VI.APLICA_FILTRO, VI.APLICA_AGRUPACION, VI.INFLUYE_BAYES, VI.CORRIGE_BAYES, VI.PESO, VI.ORDEN, I.NOMBRE_COMPLETO AS INDICADOR_DESC, V.NOMBRE AS VARIABLE_INFLUENCIA_DESC FROM MD_VARS_INF_INDICADORES VI, MD_INDICADORES I, MD_VARS_INF V
WHERE V.VARIABLE_INFLUENCIA_ID = VI.VARIABLE_INFLUENCIA_ID AND I.INDICADOR_ID = VI.INDICADOR_ID
AND VI.INDICADOR_ID = #{indicadorId}
AND VI.VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}
AND VI.APLICA_FILTRO = #{aplicaFiltro}
AND VI.APLICA_AGRUPACION = #{aplicaAgrupacion}
AND VI.INFLUYE_BAYES = #{influyeBayes}
AND VI.CORRIGE_BAYES = #{corrigeBayes}
AND VI.PESO = #{peso}

SELECT
	COUNT(*) AS COUNT
FROM
	MD_VARS_INF_INDICADORES


	AND 
		INDICADOR_ID = #{indicadorId}


	AND 
		VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}


	AND 
		APLICA_FILTRO = #{aplicaFiltro}


	AND 
		INFLUYE_BAYES = #{influyeBayes}


	AND 
		CORRIGE_BAYES = #{corrigeBayes}


	AND 
		PESO = #{peso}

mapper.delete:
DELETE FROM MD_VARS_INF_INDICADORES
WHERE
	INDICADOR_ID = #{indicadorId}
AND
	VARIABLE_INFLUENCIA_ID = #{variableInfluenciaId}
